var redis = require("../index").createClient(null, null, {
//    max_attempts: 4
});

redis.on("error", function (err) {
    EventLog.log("Redis says: " + err);
});

redis.on("ready", function () {
    EventLog.log("Redis ready.");
});

redis.on("reconnecting", function (arg) {
    EventLog.log("Redis reconnecting: " + JSON.stringify(arg));
});
redis.on("connect", function () {
    EventLog.log("Redis connected.");
});

setInterval(function () {
    var now = Date.now();
    redis.set("now", now, function (err, res) {
        if (err) {
            EventLog.log(now + " Redis reply error: " + err);
        } else {
            EventLog.log(now + " Redis reply: " + res);
        }
    });
}, 100);
