var redis = require("redis"),
    client = redis.createClient("/tmp/redis.sock"),
    profiler = require("v8-profiler");

client.on("connect", function () {
    EventLog.log("Got Unix socket connection.")
});

client.on("error", function (err) {
    EventLog.log(err.message);
});

client.set("space chars", "space value");

setInterval(function () {
    client.get("space chars");
}, 100);

function done() {
    client.info(function (err, reply) {
        EventLog.log(reply.toString());
        client.quit();
    });
}

setTimeout(function () {
    EventLog.log("Taking snapshot.");
    var snap = profiler.takeSnapshot();
}, 5000);
